package JAVA0508;

public class ex01_1 {
 //왼쪽 클래스에 저장한 이름(ex01_1)이 바뀌면 'ex01_1'이 바뀐다. 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 주석달기
				//1. 한줄주석처리
				/* 2.범위 주석 처리 */
		// ; = >>문장에 마침표 역할
		
		
		// 결과를 도출하기 위해서는 'System.out.prinln'로 결과를 도출할 수 있다.
		System.out.println("youhyunseo");//문장이 끝나면 ;로 마침표를 찍어주어야 문장이 끝났다는 것을 알 수 있음.
		System.out.println("안녕하세요!");
		
		//sysout입력후 [ctrl]+[space] ---> System.out.println()가 나온다.
		//'System.out.prinln'으로 출력할 때는 맨앞 'S'가 대문자가 되며, 뒤에 부분은 소문자 이다.
		
		System.out.println();
		//다음 줄에 빈칸을 주기 위해서는 'System.out.prinln'와 함께 (괄호)안에 아무것도 넣지 않는다.
		System.out.println("sysout입력 후 [ctrl+[space]");
		//실행(Run)=>>[ctrl]+[F11]
		//저장(save)=>>[ctrl]+[s]
		//모두 저장(save all)=>>[ctrl]+[shift]+[s]
		
		//글자 크게 [ctrl]+[+]
		//글자 작게 [ctrl]+[-]
		//글자 크기는 자판에 위쪽에 있는 [+],[-]자판을 눌러야 하며 
		//오른쪽 자판에 있는 [+],[-]을 누르면 주석이나 글자가 지워진다.
		
		/*
		 클래스 이름 바꾸기

		 * '클래스명'오른쪽 마우스 클릭 - 'Refactor'-'Rename'
		 * 프로젝트,패키지  이름 바꿀 때도 동일하게 적용 
		 */
 	}

}
